import{_ as t}from"./plugin-vue_export-helper-x3n3nnut.js";import{o as e,c as l,f as d}from"./app-VKLC1A-x.js";const n={},a=d('<h1 id="linux-操作系统概述" tabindex="-1"><a class="header-anchor" href="#linux-操作系统概述" aria-hidden="true">#</a> Linux 操作系统概述</h1><h2 id="操作系统" tabindex="-1"><a class="header-anchor" href="#操作系统" aria-hidden="true">#</a> 操作系统</h2><p>操作系统（Operating System）是管理计算机硬件与软件资源的程序</p><p>没有安装操作系统的电脑称为裸机，不管是开发还是使用都是十分不方便</p><p>操作系统就是提供了一个让用户与系统交互的操作界面</p><h2 id="操作系统分类" tabindex="-1"><a class="header-anchor" href="#操作系统分类" aria-hidden="true">#</a> 操作系统分类</h2><p>桌面操作系统：Windows、Linux、Mac</p><p>服务器操作系统：Windows、Linux</p><p>嵌入式操作系统：单片机</p><p>移动设备操作系统：Android、iOS、Harmory等</p><h2 id="linux-的起源以及版本" tabindex="-1"><a class="header-anchor" href="#linux-的起源以及版本" aria-hidden="true">#</a> Linux 的起源以及版本</h2><p>1991 年芬兰学生 Linux Torvalds 发布了 Linux 的第一个版本</p><p>Linux 的设计的初衷是要替代 Unix，模仿了 Unix 操作系统</p><p>Linux 在外观以及交互上和 Unix 非常相似</p><p>Linux 发行版本可大致分为个人桌面版和企业服务器版本</p><p>个人桌面版，Unbuntu 成熟度颇高比较受欢迎</p><p>企业服务器版本，Linux 版本更多使用的 Centos7 版本</p><h2 id="xshell" tabindex="-1"><a class="header-anchor" href="#xshell" aria-hidden="true">#</a> Xshell</h2><p>XShell 是一款 Linux 的远程连接工具，用于远程连接 Linux 服务器，方便用户对 Linux 服务器上的资源文件进行远程操控</p><p>类似的远程连接工具还有 Putty、SecureCRT，XShell 是最常用的</p><h2 id="linux-系统目录及存放的文件" tabindex="-1"><a class="header-anchor" href="#linux-系统目录及存放的文件" aria-hidden="true">#</a> Linux 系统目录及存放的文件</h2><table><thead><tr><th style="text-align:left;"><strong>目录</strong></th><th style="text-align:left;"><strong>存放的文件</strong></th></tr></thead><tbody><tr><td style="text-align:left;">bin</td><td style="text-align:left;">用于存放二进制命令</td></tr><tr><td style="text-align:left;">boot</td><td style="text-align:left;">系统内核以及引导系统程序所在目录</td></tr><tr><td style="text-align:left;">dev</td><td style="text-align:left;">所有设备文件的目录（如磁盘、光盘等）</td></tr><tr><td style="text-align:left;">etc</td><td style="text-align:left;">配置文件默认 路径，以及环境变量，服务启动命令存放路径</td></tr><tr><td style="text-align:left;">home</td><td style="text-align:left;">普通用户的家目录</td></tr><tr><td style="text-align:left;">lib</td><td style="text-align:left;">存放 32 位库文件存放目录</td></tr><tr><td style="text-align:left;">lib64</td><td style="text-align:left;">存放 64 位库文件存放目录</td></tr><tr><td style="text-align:left;">media</td><td style="text-align:left;">媒体文件存放目录</td></tr><tr><td style="text-align:left;">mnt</td><td style="text-align:left;">临时挂载设备目录</td></tr><tr><td style="text-align:left;">opt</td><td style="text-align:left;">自定义软件安装存放目录</td></tr><tr><td style="text-align:left;">proc</td><td style="text-align:left;">进程以及内核信息存放目录</td></tr><tr><td style="text-align:left;">root</td><td style="text-align:left;">root 用户的家目录路径</td></tr><tr><td style="text-align:left;">run</td><td style="text-align:left;">系统运行时产生的临时文件的存放目录</td></tr><tr><td style="text-align:left;">sbin</td><td style="text-align:left;">系统管理命令的存放目录</td></tr><tr><td style="text-align:left;">srv</td><td style="text-align:left;">服务启动之后需要访问的数据目录</td></tr><tr><td style="text-align:left;">sys</td><td style="text-align:left;">系统使用目录</td></tr><tr><td style="text-align:left;">tmp</td><td style="text-align:left;">临时文件目录</td></tr><tr><td style="text-align:left;">usr</td><td style="text-align:left;">系统命令和帮助文档路径</td></tr><tr><td style="text-align:left;">var</td><td style="text-align:left;">存放内存易变的文件的目录</td></tr></tbody></table>',22),i=[a];function r(s,x){return e(),l("div",null,i)}const y=t(n,[["render",r],["__file","01_Linux操作系统概述.html.vue"]]);export{y as default};
